#include <iostream>
#include <string>

std::string join(const std::vector<std::string>& v, const std::string& delim) {
    std::string s;
    for (size_t i = 0; i < v.size(); ++i) {
        if (i > 0) s += delim;
        s += v[i];
    }
    return s;
}

