  // Dart dart:io doesn't have native UTF-16 sink helper easily accessible
  // Using manual byte conversion (LE)
  List<int> bytes = [];
  for (var codeUnit in text.codeUnits) {
    bytes.add(codeUnit & 0xFF);
    bytes.add((codeUnit >> 8) & 0xFF);
  }
  await File(path).writeAsBytes(bytes);
}

