Future<bool> fileEqualsSync(String p1, String p2) async {
  var f1 = File(p1);
  var f2 = File(p2);
  if (f1.lengthSync() != f2.lengthSync()) return false;
  var b1 = f1.readAsBytesSync();
  var b2 = f2.readAsBytesSync();
  for (var i = 0; i < b1.length; i++) if (b1[i] != b2[i]) return false;
  return true;
}

