import 'dart:io';

Future<void> splitSync(String src, int parts) async {
  var bytes = File(src).readAsBytesSync();
  var chunk = (bytes.length / parts).ceil();
  for (var i = 0; i < parts; i++) {
    var start = i * chunk;
    var end = (start + chunk < bytes.length) ? start + chunk : bytes.length;
    if (start < end) File('$src.$i').writeAsBytesSync(bytes.sublist(start, end));
  }
}

