Future<void> pipeStdInToFile(String path) async {
  var f = File(path).openSync(mode: FileMode.write);
  f.writeFromSync(stdin.readByteSync() as List<int>); // Conceptual, stdin sync read is line based usually
  f.closeSync();
}

