Future<void> writeInt8(String path, int i) async {
  await File(path).writeAsBytes([i & 0xFF]);
}

import 'dart:io';

Future<void> writeInt16(String path, int i) async {
  var b = BytesBuilder();
  b.addByte((i >> 8) & 0xFF);
  b.addByte(i & 0xFF);
  await File(path).writeAsBytes(b.toBytes());
}

import 'dart:io';

Future<void> writeInt32(String path, int i) async {
  var b = BytesBuilder();
  b.addByte((i >> 24) & 0xFF);
  b.addByte((i >> 16) & 0xFF);
  b.addByte((i >> 8) & 0xFF);
  b.addByte(i & 0xFF);
  await File(path).writeAsBytes(b.toBytes());
}

