import 'dart:io';

Future<void> writeLargeSync(String path, int mb) async {
  var f = File(path).openSync(mode: FileMode.write);
  var chunk = List.filled(1024 * 1024, 0);
  for (var i = 0; i < mb; i++) f.writeFromSync(chunk);
  f.closeSync();
}

import 'dart:io';

Future<void> readLargeSync(String path) async {
  var f = File(path).openSync();
  var buf = List<int>.filled(1024 * 1024, 0);
  while (f.readIntoSync(buf) > 0) {}
  f.closeSync();
}

