Future<void> writeLinesBuffered(String path, List<String> lines) async {
  var sink = File(path).openWrite();
  for (var l in lines) sink.writeln(l);
  await sink.close();
}

import 'dart:io';

Future<void> copyBuffered(String src, String dest) async {
  var instream = File(src).openRead();
  var outstream = File(dest).openWrite();
  await outstream.addStream(instream);
  await outstream.close();
}

import 'dart:io';

Future<void> lockRead(String path) async {
  var f = await File(path).open();
  await f.lock(FileLock.shared);
  await f.unlock();
  await f.close();
}

import 'dart:io';

Future<void> lockWrite(String path) async {
  var f = await File(path).open(mode: FileMode.write);
  await f.lock(FileLock.exclusive);
  await f.unlock();
  await f.close();
}

import 'dart:io';

Future<bool> checkPermission(String path) async {
  // Dart doesn't have AccessControl, assume if stat works we have some access
  try {
    await File(path).stat();
    return true;
  } catch (e) {
    return false;
  }
}

