program BinarySearchFile;
var
  f: file of Integer;
  L, R, M, val, target: Integer;
begin
  target := 50;
  AssignFile(f, 'sorted.dat'); Reset(f);
  L := 0; R := FileSize(f) - 1;
  while L <= R do
  begin
    M := (L + R) div 2;
    Seek(f, M); Read(f, val);
    if val = target then begin Writeln('Found at ', M); Break; end
    else if val < target then L := M + 1
    else R := M - 1;
  end;
  CloseFile(f);
end.

