program CheckPrime;
function IsPrime(n: Integer): Boolean;
var
  i: Integer;
begin
  if n < 2 then Exit(False);
  for i := 2 to Trunc(Sqrt(n)) do
    if (n mod i) = 0 then Exit(False);
  Result := True;
end;
begin
  if IsPrime(17) then Writeln('Prime') else Writeln('Not Prime');
end.

program BubbleSort;
var
  arr: array[1..5] of Integer = (5, 3, 8, 1, 2);
  i, j, temp: Integer;
begin
  for i := 1 to 4 do
    for j := 1 to 5 - i do
      if arr[j] > arr[j + 1] then
      begin
        temp := arr[j];
        arr[j] := arr[j + 1];
        arr[j + 1] := temp;
      end;
  for i := 1 to 5 do Write(arr[i], ' ');
end.

program PalindromeCheck;
function IsPalindrome(s: String): Boolean;
var
  i, len: Integer;
begin
  len := Length(s);
  Result := True;
  for i := 1 to len div 2 do
    if s[i] <> s[len - i + 1] then Exit(False);
end;
begin
  if IsPalindrome('radar') then Writeln('Yes') else Writeln('No');
end.

