program GCDCalc;
function GCD(a, b: Integer): Integer;
var temp: Integer;
begin
  while b <> 0 do
  begin
    temp := b;
    b := a mod b;
    a := temp;
  end;
  Result := a;
end;
begin
  Writeln('GCD of 48 and 18: ', GCD(48, 18));
end.

