function readLastLines($file, $lines) {
    $handle = fopen($file, "r");
    $lineCounter = $lines;
    $pos = -2;
    $beginning = false;
    $text = [];
    while ($lineCounter > 0) {
        $t = " ";
        while ($t != "\n") {
            if(fseek($handle, $pos, SEEK_END) == -1) {
                $beginning = true;
                break;
            }
            $t = fgetc($handle);
            $pos --;
        }
        $lineCounter --;
        if ($beginning) rewind($handle);
        $text[$lines - $lineCounter - 1] = fgets($handle);
        if ($beginning) break;
    }
    fclose($handle);
    return array_reverse($text);
}

