function globRecursive($pattern, $flags = 0) {
    $files = glob($pattern, $flags);
    foreach (glob(dirname($pattern).'/*', GLOB_ONLYDIR|GLOB_NOSORT) as $dir) {
        $files = array_merge($files, globRecursive($dir.'/'.basename($pattern), $flags));
    }
    return $files;
}

function chmodRecursive($dir, $dirPermissions, $filePermissions) {
    $dp = opendir($dir);
    while($file = readdir($dp)) {
        if (($file == ".") || ($file == "..")) continue;
        $fullPath = $dir . "/" . $file;
        if (is_dir($fullPath)) {
            chmod($fullPath, $dirPermissions);
            chmodRecursive($fullPath, $dirPermissions, $filePermissions);
        } else {
            chmod($fullPath, $filePermissions);
        }
    }
    closedir($dp);
}

