def count_words_in_file(file_path):
    word_count = 0
    try:
        with open(file_path, 'r', encoding='utf-8') as file:
            for line in file:
                words = line.split()
                word_count += len(words)
    except FileNotFoundError:
        return "File not found."
    except IOError:
        return "Error reading file."
    return word_count
