}

class Person {
    has $.name;
    has $.age;
}
my $p = Person.new(name => 'Alice', age => 30);

try {
    die "Something wrong";
    CATCH {
        default { say "Caught error: " ~ .message }
    }
}

sub factorial($n) {
    return 1 if $n <= 1;
    return $n * factorial($n - 1);
}

grammar Calculator {
    token TOP { <num> '+' <num> }
    token num { d+ }
}
say Calculator.parse("1+2");

my $n = 7;
if $n == none(2, 4, 6) {
    say "Not an even number in list";
}

role Flying {
    method fly { say "Flying" }
}
class Bird does Flying {}
Bird.new.fly;

class Parent {
    method greet { say "Hello" }
}
class Child is Parent {}
Child.new.greet;

class Singleton {
    my $instance;
    method new { $instance //= self.bless } 
}

